import React, { useState } from "react";
//useState is a function inside the react library which requires an initial state which could be a boolean or a string or an array
const UseStateBasics = () => {
  // console.log(useState('hello world!'));
  //useState when invoked returns an array which contains the argument 
  // useState('something') returns an array so we can store it in a variable
  const value = useState(1);
  console.log(value);
  return <h2>useState basic example</h2>;
};

export default UseStateBasics;
