import React, {useState} from 'react';

const UseStateCounter = () => {
  const [value, setValue] = useState(0);

  const updateCounter = () => {
    setValue(value + 1);
  };

  return (
    <>
      <section style={{margin:'4rem 0'}}>
        <h1>Regular Counter</h1>
        <h2>{value}</h2>
        <button className='btn' onClick={updateCounter}>Increase</button>
        <button className='btn' onClick={updateCounter}>Decrease</button>
        <button className='btn' onClick={updateCounter}>Reset</button>
      </section>
    </>
  )
};

export default UseStateCounter;
