import React from 'react';
import { data } from '../../../data';

const UseStateArray = () => {
  const [people, setPeople] = React.useState(data);
  return (
    <React.Fragment>
      {people.map(person => {
        const {id, name} = person;
        console.log(person);
        <Person data={id, name}></Person>
      })}
    </React.Fragment>
  )
};

const Person = ({data}) => {
  return (
    <article>
      <h1>{data.name}</h1>
      <h3>{data.id}</h3>
    </article>
  );
};

export default UseStateArray;
