import React from "react";
import { data } from "../../../data";

const UseStateArray = () => {
  let [people, setPeople] = React.useState(data);

  const clickHandler = (name) => {
    console.log(`inside ${name}`);
    people = people.filter(person => person.name !== name);
    setPeople(people);
  };

  return (
    <>
      {people.map(person => {
        const {id, name} = person;
        return (
          <div key={id} className='item'>
            <h4>{name}</h4>
            <button className="btn" onClick={() => clickHandler(name)}>Remove Item</button>
          </div>
        )
      })}
      <button type='button' className="btn" disabled={people.length > 0 ? true:false} onClick={clickHandler}>Delete</button>
    </>
  );
};

export default UseStateArray;
