import React from "react";
import { data } from "../../../data";

const UseStateArray = () => {
  const [people, setPeople] = React.useState(data);
  {
    people.map((person) => {
      return (
        <>
          <div key={person.id} className="item">
            <h4></h4>
          </div>
        </>
      );
    });
  }
};

export default UseStateArray;
