import React from 'react';
import { data } from '../../../data';

const UseStateArray = () => {
  const [people, setPeople] = React.useState(data);
  return (
    <React.Fragment>
      {people.map(person => {
        return (
        <Person data={{...person}}></Person>
        );
      })}
    </React.Fragment>
  )
};

const Person = ({data}) => {
  return (
    <article>
      <h1><h3 style={{display:'inline'}}>{data.id} </h3>{data.name}</h1>
    </article>
  );
};

export default UseStateArray;
