import React from 'react';
import { data } from '../../../data';

const UseStateArray = () => {
  const [person, setPerson] = React.useState(data);
  console.log(person, setPerson);
  const clickHandler = () => {
    setPerson('string');
    console.log(person);  
  };
  return (
    <React.Fragment>
      <h2 onClick={clickHandler}>UseStateArray Example</h2>
    </React.Fragment>
  )
};

export default UseStateArray;
