import React from "react";

let title = "Random Title";
const ErrorExample = () => {
  const clickHandler = () => {
    title = 'Hello World!';
  }; 

  return (
    <React.Fragment>
      <h2>{title}</h2>
      <button className="btn" onClick={clickHandler}></button>
    </React.Fragment>
  );
};
