import React, { useState, useEffect } from 'react';
// by default useEffect runs after every re-render
// cleanup function
// second parameter

//The useEffect() hook allows you to work on side-effects, basically anyting that is outside your component

//The way useEffect() works is that you pass in a callback that runs after every render
const UseEffectBasics = () => {
  useEffect(() => {
    console.log("useEffect");
  });

  console.log('render component');
  return <h2>useEffect Basics</h2>;
};

export default UseEffectBasics;
