import React, { useState, useEffect } from 'react';
import { data } from '../../../data';
import { Link, useParams } from 'react-router-dom';
const Person = () => {
  const {id} = useParams();
  console.log(id);
  const person = data.map(person => person.id === id);
  console.log(person);
  return (
    <div>
      <h2>{person.name}</h2>
    </div>
  );
};

export default Person;
