import React, { useState, useReducer } from "react";
import { data } from "../../../data";
// more components
// fix - context api, redux (for more complex cases)

//Prop-drilling is basically a sideeffect when we have multiple components and a component tree and you need to start passing some state from the top component to the bottom component of the component tree

const defaultState = {
  people: [],
  isModalOpen: false,
  modalContent: "",
};

const reducer = (state, action) => {
  if(action.type === 'ADD_ITEM'){
    const newPeople = [...state.people, action.payload];
    console.log(newPeople);
    return {...state, people:newPeople};
  }
  else if(action.type === 'REMOVE_ITEM'){
    const newPeople = state.people.filter(person => person.id !== action.payload);
    return {...state, people:newPeople};
  }
};

const PropDrilling = () => {
  const [state, dispatch] = useReducer(reducer, defaultState);
  const [input, setInput] = useState('');

const submitHandler = (e) => {
  e.preventDefault();
  if(input){
    dispatch({type:'ADD_ITEM', payload:{id:new Date().getTime().toString(), input}});
    setInput('');
  }
};

  return (
    <>
      <form className="form" onSubmit={submitHandler}>
        <div>
          <input type="text" value={input} onChange={(e) => setInput(e.target.value)} />
        </div>
      <button type="button" className="btn">Add Person</button>
      </form>
      <section>
        <h3>prop drilling</h3>
        <List people={state.people} dispatch={dispatch} />
      </section>
    </>
  );
};

const List = ({ people, dispatch }) => {
  return (
    <>
      {people.map((person) => {
        return <SinglePerson key={person.id} {...person} dispatch={dispatch} />;
      })}
    </>
  );
};

const SinglePerson = ({ id, name, dispatch }) => {
  return (
    <div className="item">
      <h4>{name}</h4>
      <button type='button' onClick={() => dispatch({type:'REMOVE_ITEM'})}>Remove</button>
    </div>
  );
};

export default PropDrilling;
