  return (
    <>
      <section style={{margin:'4rem 0'}}>
        <h1>Regular Counter</h1>
        <h2>{counter}</h2>
        <button className='btn' onClick={updateCounter}>Update Counter</button>
      </section>
    </>
  )
};

